const modal = document.getElementById('story-modal');
const closeBtn = document.querySelector('.close-btn');

function openModal(story) {
    document.getElementById('modal-title').innerText = story.title;
    document.getElementById('modal-icon').innerText = story.icon;
    document.getElementById('modal-story').innerText = story.story;
    document.getElementById('modal-moral').innerText = story.moral;
    modal.style.display = 'block';
    document.body.style.overflow = 'hidden'; // ‡¶¨‡ßç‡¶Ø‡¶æ‡¶ï‡¶ó‡ßç‡¶∞‡¶æ‡¶â‡¶®‡ßç‡¶° ‡¶∏‡ßç‡¶ï‡ßç‡¶∞‡¶≤ ‡¶¨‡¶®‡ßç‡¶ß ‡¶ï‡¶∞‡¶æ
}

closeBtn.onclick = function() {
    modal.style.display = "none";
    document.body.style.overflow = "auto";
}

window.onclick = function(event) {
    if (event.target == modal) {
        modal.style.display = "none";
        document.body.style.overflow = "auto";
    }
}
